// Warm Intro Finder - Graph Matching Logic

// Sample contacts data (in production, fetch from Google Sheets or Airtable)
const contacts = [
  {
    "name": "Alice Thompson",
    "company": "Acme Corp",
    "email": "alice.thompson@acmecorp.com",
    "title": "Director of Engineering",
    "relationship_type": "former_colleague",
    "connection_strength": "strong",
    "last_contact": "2023-12-10",
    "notes": "Worked together at previous company for 3 years"
  },
  {
    "name": "Bob Wilson",
    "company": "TechStart Inc",
    "email": "bob.wilson@techstart.com",
    "title": "Senior Developer",
    "relationship_type": "university_friend",
    "connection_strength": "medium",
    "last_contact": "2023-11-15",
    "notes": "College roommate, stays in touch occasionally"
  },
  {
    "name": "Carol Davis",
    "company": "DataFlow Systems",
    "email": "carol.davis@dataflow.com",
    "title": "VP of Operations",
    "relationship_type": "industry_contact",
    "connection_strength": "medium",
    "last_contact": "2023-10-20",
    "notes": "Met at industry conference, exchanged business cards"
  },
  {
    "name": "Emma Foster",
    "company": "CloudVision Ltd",
    "email": "emma.foster@cloudvision.com",
    "title": "Senior Manager",
    "relationship_type": "mutual_connection",
    "connection_strength": "weak",
    "last_contact": "2023-09-05",
    "notes": "Connected through LinkedIn mutual friend"
  },
  {
    "name": "Henry Kim",
    "company": "InnovateLab",
    "email": "henry.kim@innovatelab.com",
    "title": "Co-founder",
    "relationship_type": "investor_network",
    "connection_strength": "medium",
    "last_contact": "2023-11-30",
    "notes": "Met through investor network events"
  }
];

// Get the new lead data from the trigger
const leadData = $input.all()[0].json;

console.log('Processing new lead:', leadData);

// Find matching contacts at the same company
const matchingContacts = contacts.filter(contact => 
  contact.company.toLowerCase() === leadData.Company.toLowerCase()
);

console.log('Found matching contacts:', matchingContacts);

let result = {
  ...leadData,
  relationship_status: 'cold_outreach',
  connection_details: null,
  connection_strength: null,
  intro_message_template: null
};

if (matchingContacts.length > 0) {
  // Sort by connection strength (strong > medium > weak)
  const strengthOrder = { 'strong': 3, 'medium': 2, 'weak': 1 };
  const bestContact = matchingContacts.sort((a, b) => 
    strengthOrder[b.connection_strength] - strengthOrder[a.connection_strength]
  )[0];

  result.relationship_status = 'warm_intro_possible';
  result.connection_details = `${bestContact.name} (${bestContact.relationship_type})`;
  result.connection_strength = bestContact.connection_strength;
  result.contact_name = bestContact.name;
  result.contact_email = bestContact.email;
  result.contact_title = bestContact.title;
  result.relationship_notes = bestContact.notes;

  // Generate intro message template based on relationship type
  const templates = {
    'former_colleague': `Hi ${bestContact.name}, I hope you're doing well! I wanted to reach out about someone who might be a great fit for ${leadData.Company}. ${leadData.Name} is the ${leadData.Title} there, and I think you two should connect.`,
    'university_friend': `Hey ${bestContact.name}! Hope all is well. I came across ${leadData.Name} who works as ${leadData.Title} at ${leadData.Company}. Given your role there, I thought you might want to connect.`,
    'industry_contact': `Hi ${bestContact.name}, I hope you're doing well. I wanted to introduce you to ${leadData.Name}, who is the ${leadData.Title} at ${leadData.Company}. I think there could be some great synergy.`,
    'mutual_connection': `Hi ${bestContact.name}, I hope this finds you well. I'd like to introduce you to ${leadData.Name} from ${leadData.Company}. I think you two should connect.`,
    'investor_network': `Hi ${bestContact.name}, hope you're doing great! I wanted to connect you with ${leadData.Name}, the ${leadData.Title} at ${leadData.Company}. I think there might be some interesting opportunities to explore.`
  };

  result.intro_message_template = templates[bestContact.relationship_type] || templates['industry_contact'];
}

console.log('Final result:', result);

return [result];

